INSERT INTO `tsys_config` (`type`,`ckey`,`cvalue`,`updater`,`update_datetime`,`remark`,`company_code`,`system_code`) VALUES ('sys_txt','about_us','<h3 style=\"text-align: center; \">户外电商关于我们</h3><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;生活在这个时代，你要是说你不梦想着旅行都不好意思出去跟人打招呼....&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那么我们，从什么时候开始，空虚到竟然把旅行当做了梦想？&nbsp;</p><p><img src=\"http://ov30dsi08.bkt.clouddn.com/QQ图片20170914100455_1505400872375.png\" style=\"max-width:100%\"><br></p><p><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;打开人人，满屏都是几个字：我要旅行！赤裸裸明晃晃，刺的我眼睛睁不开的疼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不可否认，旅行的确很被向往，它对一个人意义无穷。想起被无数人奉为旅行至理名言的几句励志语，摘抄如下，与君共勉：&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1如果你不出去走走，你会以为，这就是世界。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2有些事你现在不做，就永远不会去做了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3旅行的意义不在于你遇到多少人，见到多少未知的风景，而是在某个瞬间突然的认识了自己……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4一个人的一生，一定要有一次说走就走的旅行。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;5要么读书，要么旅行，灵魂和身体，一定要有一个在路上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我当然不会蠢到去觉得这些话有什么问题，。励志嘛，在这个年头，又不是多值钱的事情。芙蓉姐姐摇身一变，给大家励了志；林丹拿了个大满贯，大家觉得很亢奋；莫言静悄悄，也终于圆了中国人一个情节.....&nbsp;</p><p><br></p>','admin',now(),'关于我们','CD-CHW000015','CD-CHW000015');
