INSERT INTO `tsys_config` (`type`,`ckey`,`cvalue`,`updater`,`update_datetime`,`remark`,`company_code`,`system_code`) VALUES ('sys_txt','xuexin_guide','<h4 style=\"text-align: center; text-indent: 0px;\">学生认证引导</h4><p>1，这个学生免押金仅适用于在校大学生；</p><p>2，如果您是在校大学生，需要去学信网下载/截取一张你的学籍信息，上传的我们平台证明您是未毕业的学生。图片样式参考下图；</p><p><img src=\"http://ov30dsi08.bkt.clouddn.com/QQ图片20170914230925_1505401784568.png\" style=\"max-width:100%\"><br></p><p>3，上传之后，平台会在1-2个工作日予以处理，如果您确实是在在校学生，那么恭喜您，将会享受我们的免押金租赁商品的优惠。</p>','admin',now(),'关于我们','CD-CHW000015','CD-CHW000015');
